def format_separated_words(words):
    separated_words = {}
    for word in words:
        initial = word[0].lower()
        if initial not in separated_words:
            separated_words[initial] = []
        separated_words[initial].append(word)
    
    formatted_output = ""
    for initial, word_list in sorted(separated_words.items()):
        formatted_output += f"{initial}: {', '.join(word_list)}\n"
    return formatted_output

