def merge_and_sort_lists(*lists):
    # Combine all lists into one list
    combined_list = []
    for lst in lists:
        combined_list.extend(lst)
    
    # Sort the combined list alphabetically
    sorted_list = sorted(combined_list)
    
    return sorted_list

